mapper.select:
        select  t.rule_name,t.rule_name ruleName,t.remark,t.status_cd,t.status_cd statusCd,t.rule_id,t.rule_id ruleId,t.community_id,t.community_id communityId 
from integral_rule t 
where 1 =1 

   and t.rule_name= #{ruleName}


   and t.remark= #{remark}


   and t.status_cd= #{statusCd}


   and t.rule_id= #{ruleId}


   and t.community_id= #{communityId}

order by t.create_time desc

   limit #{page}, #{row}

mapper.update:
        update  integral_rule t set t.status_cd = #{statusCd}

,t.b_id = #{newBId}


, t.rule_name= #{ruleName}


, t.remark= #{remark}


, t.community_id= #{communityId}

 where 1=1 
and t.rule_id= #{ruleId}

mapper.select:
        select  count(1) count 
from integral_rule t 
where 1 =1 

   and t.rule_name= #{ruleName}


   and t.remark= #{remark}


   and t.status_cd= #{statusCd}


   and t.rule_id= #{ruleId}


   and t.community_id= #{communityId}
