mapper.select:
        select  t.request_message,t.request_message requestMessage,t.cost_time,t.cost_time costTime,t.request_url,t.request_url requestUrl,t.request_method,t.request_method requestMethod,t.response_header,t.response_header responseHeader,t.log_id,t.log_id logId,t.request_header,t.request_header requestHeader,t.state,t.response_message,t.response_message responseMessage 
from transaction_out_log t 
where 1 =1 

   and t.request_message= #{requestMessage}


   and t.cost_time= #{costTime}


   and t.request_url= #{requestUrl}


   and t.request_method= #{requestMethod}


   and t.response_header= #{responseHeader}


   and t.log_id= #{logId}


   and t.request_header= #{requestHeader}


   and t.state= #{state}


   and t.response_message= #{responseMessage}

order by t.create_time desc

   limit #{page}, #{row}

mapper.update:
        update  transaction_out_log t set t.status_cd = #{statusCd}

,t.b_id = #{newBId}


, t.request_message= #{requestMessage}


, t.cost_time= #{costTime}


, t.request_url= #{requestUrl}


, t.request_method= #{requestMethod}


, t.response_header= #{responseHeader}


, t.request_header= #{requestHeader}


, t.state= #{state}


, t.response_message= #{responseMessage}

 where 1=1 
and t.log_id= #{logId}

mapper.select:
        select  count(1) count 
from transaction_out_log t 
where 1 =1 

   and t.request_message= #{requestMessage}


   and t.cost_time= #{costTime}


   and t.request_url= #{requestUrl}


   and t.request_method= #{requestMethod}


   and t.response_header= #{responseHeader}


   and t.log_id= #{logId}


   and t.request_header= #{requestHeader}


   and t.state= #{state}


   and t.response_message= #{responseMessage}
