mapper.select:
        select  t.departure_time,t.departure_time departureTime,t.v_name,t.v_name vName,t.status_cd,t.status_cd statusCd,t.ps_id,t.ps_id psId,t.visit_gender,t.visit_gender visitGender,t.owner_id,t.owner_id ownerId,t.reason_type,t.reason_type reasonType,t.user_id,t.user_id userId,t.state_remark,t.state_remark stateRemark,t.v_id,t.v_id vId,t.visit_time,t.visit_time visitTime,t.phone_number,t.phone_number phoneNumber,t.free_time,t.free_time freeTime,t.visit_case,t.visit_case visitCase,t.state,t.community_id,t.community_id communityId,t.entourage,t.record_state,t.record_state recordState 
from s_visit_info t 
where 1 =1 

   and t.departure_time= #{departureTime}


   and t.v_name= #{vName}


   and t.status_cd= #{statusCd}


   and t.ps_id= #{psId}


   and t.visit_gender= #{visitGender}


   and t.owner_id= #{ownerId}


   and t.reason_type= #{reasonType}


   and t.user_id= #{userId}


   and t.state_remark= #{stateRemark}


   and t.v_id= #{vId}


   and t.visit_time= #{visitTime}


   and t.phone_number= #{phoneNumber}


   and t.free_time= #{freeTime}


   and t.visit_case= #{visitCase}


   and t.state= #{state}


   and t.community_id= #{communityId}


   and t.entourage= #{entourage}


   and t.record_state= #{recordState}

order by t.create_time desc

   limit #{page}, #{row}

mapper.update:
        update  s_visit_info t set t.status_cd = #{statusCd}

,t.b_id = #{newBId}


, t.departure_time= #{departureTime}


