mapper.select:
select t.original_stock,t.original_stock originalStock,t.quantity,t.create_time,t.create_time
createTime,t.rs_id,t.rs_id rsId,t.apply_order_id,t.apply_order_id applyOrderId,t.status_cd,t.status_cd
statusCd,t.remark,t.id,t.state,t.res_id,t.res_id resId
from asset_inventory_detail t
where 1 =1

    and t.res_id= #{resId}


    and t.original_stock= #{originalStock}


    and t.quantity= #{quantity}


    and t.create_time= #{createTime}


    and t.rs_id= #{rsId}


    and t.apply_order_id= #{applyOrderId}


    and t.status_cd= #{statusCd}


    and t.remark= #{remark}


    and t.id= #{id}


    and t.state= #{state}

order by t.create_time desc

    limit #{page}, #{row}

mapper.update:
update asset_inventory_detail t set t.status_cd = #{statusCd}

    , t.res_id= #{resId}


    , t.original_stock= #{originalStock}


    , t.quantity= #{quantity}


    , t.create_time= #{createTime}


    , t.rs_id= #{rsId}



    , t.remark= #{remark}


    , t.state= #{state}

where 1=1

    and t.id= #{id}


    and t.apply_order_id= #{applyOrderId}

mapper.select:
select p.id, p.apply_order_id applyOrderId, p.res_id resId,p.quantity,p.remark, p.original_stock originalStock,p.state, r.res_name resName,r.res_code resCode, r.price,r.price standardPrice,r.stock,rst.name rstName,rst1.name parentRstName,rss.spec_name specName,rs.supplier_name supplierName, td1.name unitCodeName, td2.name miniUnitCodeName, r.is_fixed isFixed, td6.name isFixedName from asset_inventory_detail p inner join resource_store r on p.res_id = r.res_id and r.status_cd = '0' left join t_dict td1 on r.unit_code = td1.status_cd and td1.table_name = 'resource_store' and td1.table_columns = 'unit_code' left join t_dict td2 on r.mini_unit_code = td2.status_cd and td2.table_name = 'resource_store' and td2.table_columns = 'unit_code' left join t_dict td6 on r.is_fixed = td6.status_cd and td6.table_name = 'resource_store' and td6.table_columns = 'is_fixed' LEFT JOIN resource_supplier rs on p.rs_id=rs.rs_id LEFT JOIN resource_store_type rst on r.rst_id=rst.rst_id LEFT JOIN resource_store_type rst1 on r.parent_rst_id=rst1.rst_id LEFT JOIN resource_store_specification rss on r.rss_id=rss.rss_id where 1=1 and p.status_cd = '0'
and p.apply_order_id in
#{item}
and p.apply_order_id= #{applyOrderId}

select count(1) count
from asset_inventory_detail t
where 1 =1

    and t.original_stock= #{originalStock}


    and t.res_id= #{resId}


    and t.quantity= #{quantity}


    and t.create_time= #{createTime}


    and t.rs_id= #{rsId}


    and t.apply_order_id= #{applyOrderId}


    and t.status_cd= #{statusCd}


    and t.remark= #{remark}


    and t.id= #{id}


    and t.state= #{state}
