mapper.select:
        select  t.create_user_id,t.create_user_id createUserId,t.res_order_type,t.res_order_type resOrderType,t.end_user_name,t.end_user_name endUserName,t.description,t.apply_order_id,t.apply_order_id applyOrderId,t.status_cd,t.status_cd statusCd,t.create_user_name,t.create_user_name createUserName,t.store_id,t.store_id storeId,t.user_name,t.user_name userName,t.user_id,t.user_id userId,t.warehousing_way,t.warehousing_way warehousingWay,t.state,t.community_id,t.community_id communityId,t.end_user_tel,t.end_user_tel endUserTel 
from purchase_apply t 
where 1 =1 

   and t.create_user_id= #{createUserId}


   and t.res_order_type= #{resOrderType}


   and t.end_user_name= #{endUserName}


   and t.description= #{description}


   and t.apply_order_id= #{applyOrderId}


   and t.status_cd= #{statusCd}


   and t.create_user_name= #{createUserName}


   and t.store_id= #{storeId}


   and t.user_name= #{userName}


   and t.user_id= #{userId}


   and t.warehousing_way= #{warehousingWay}


   and t.state= #{state}


   and t.community_id= #{communityId}


   and t.end_user_tel= #{endUserTel}

order by t.create_time desc

   limit #{page}, #{row}

mapper.update:
        update  purchase_apply t set t.status_cd = #{statusCd}

,t.b_id = #{newBId}


, t.create_user_id= #{createUserId}


, t.res_order_type= #{resOrderType}


, t.end_user_name= #{endUserName}


, t.description= #{description}


, t.create_user_name= #{createUserName}


, t.store_id= #{storeId}


, t.user_name= #{userName}


, t.user_id= #{userId}


, t.warehousing_way= #{warehousingWay}


, t.state= #{state}


, t.community_id= #{communityId}


, t.end_user_tel= #{endUserTel}

 where 1=1 
and t.apply_order_id= #{applyOrderId}

mapper.select:
        select  count(1) count 
from purchase_apply t 
where 1 =1 

   and t.create_user_id= #{createUserId}


   and t.res_order_type= #{resOrderType}


   and t.end_user_name= #{endUserName}


   and t.description= #{description}


   and t.apply_order_id= #{applyOrderId}


   and t.status_cd= #{statusCd}


